import React from 'react'
import Square from './square'

class Board extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            squares: Array(9).fill(null),
        }
    }

    renderSquare(i) {
        return <Square value={i} />
    } 

    render () {
        return (
        <div>
            <div className='board-row'> 
                { this.renderSquare(1) }
                { this.renderSquare(2) }
                { this.renderSquare(3) }
            </div>
            <div> 
                { this.renderSquare(1) }
                { this.renderSquare(2) }
                { this.renderSquare(3) }
            </div>
            <div> 
                { this.renderSquare(1) }
                { this.renderSquare(2) }
                { this.renderSquare(3) }
            </div>
            {/* <div>
                <Square value='1' />
                <Square value='2' />
                <Square value='3' />
            </div> */}
        </div>
        )
    }
}

export default Board
