import React from 'react'
import Square from './square'

class Board extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            squares: Array(9).fill(null),
            xIsNext: true,
        }
    }

    renderSquare(i) {
        return (
            <Square 
                value={this.state.squares[i]}
                onClick={() => this.handleClick(i)}
            />
        )
    }

    renderSquare(i) {
        return (
            <Square 
                value = {this.state.squares[i]}
            />
        )
    }

    handleClick(i) {
        const newSquares = this.state.squares
        newSquares[i] = this.state.xIsNext ? 'X': 'O'
        this.setState({squares: newSquares, xIsNext: !this.state.xIsNext})
    }

    render () {
        return (
        <div>
            <div>Next player {this.state.xIsNext ? 'X': 'O'}</div>
            <div className='board-row'> 
                { this.renderSquare(0) }
                { this.renderSquare(1) }
                { this.renderSquare(2) }
            </div>
            <div> 
                { this.renderSquare(3) }
                { this.renderSquare(4) }
                { this.renderSquare(5) }
            </div>
            <div> 
                { this.renderSquare(6) }
                { this.renderSquare(7) }
                { this.renderSquare(8) }
            </div>
            {/* <div>
                <Square value='1' />
                <Square value='2' />
                <Square value='3' />
            </div> */}
        </div>
        )
    }
}

export default Board
